import React, { useState, useContext } from "react";
import { Form, FormGroup, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import "./transferBooking.css";
import { BASE_URL } from "../../utils/config";


const TransferBooking = ({ transfer }) => {
  const { price } = transfer;
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const initialBookingState = {
    userId: user ? user._id : null,
    userEmail: user ? user.email : "",
    transferFrom: "",
    transferTo: "",
    flightNumber: "",
    fullName: "",
    email: "",
    phone: "",
    passengerNames: "",
    transferDate: "",
    transferTime: "",
    note: ""
  };

  const [booking, setBooking] = useState(initialBookingState);

  const handleChange = e => {
    setBooking(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleClick = async e => {
    e.preventDefault();

    try {
        const res = await fetch(`${BASE_URL}/transfer-booking`, {
            method: "post",
            headers: {"content-type": "application/json"},
            body: JSON.stringify(booking)
        });
        const result = await res.json();
        if (!res.ok) {
          throw new Error(result.message);
        }
        navigate("/thank-you");
    } catch (error) {
      console.error('Booking error:', error.message);
      // handle error
    }
  };


  return (
    <Form onSubmit={handleClick} className="booking-form">
      {/* Personal Information Fields */}
      <FormGroup>
        <input
          type="text"
          name="fullName"
          placeholder="Name Surname"
          required
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="passengerNames"
          placeholder="Passengers Names"
          onChange={handleChange}
        />
      </FormGroup>

      {/* Transfer Information Fields */}
      <FormGroup>
        <input
          type="text"
          name="transferFrom"
          placeholder="From"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="transferTo"
          placeholder="To"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="flightNumber"
          placeholder="Flight number"
          onChange={handleChange}
        />
        <input
          type="date"
          name="transferDate"
          required
          onChange={handleChange}
        />
        <input
          type="time"
          name="transferTime"
          required
          onChange={handleChange}
        />
        <textarea
          name="note"
          placeholder="Note"
          onChange={handleChange}
        />
      </FormGroup>

      <Button type="submit" className="btn primary__btn">
        Book Now - ${price}
      </Button>
    </Form>
  );
};

export default TransferBooking;
