import React, { useState, useContext } from "react";
import { Form, FormGroup, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import "./transferBooking.css";
import { BASE_URL } from "../../utils/config";

const TransferBooking = ({ transfer }) => {
  const { price } = transfer;
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const [booking, setBooking] = useState({
    userId: user ? user._id : null,
    userEmail: user ? user.email : "",
    transferFrom: transfer.origin,
    transferTo: transfer.destination,
    flightNumber: "",
    fullName: "",
    address: "",
    phone: "",
    passengerNames: "",
    transferDate: "",
    transferTime: "",
    note: ""
  });

  const handleSwap = () => {
    setBooking(prevBooking => ({
      ...prevBooking,
      transferFrom: prevBooking.transferTo,
      transferTo: prevBooking.transferFrom
    }));
  };

  const handleChange = e => {
    setBooking(prev => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleClick = async e => {
    e.preventDefault();

    try {
      const res = await fetch(`${BASE_URL}/transfer-booking`, {
        method: "post",
        headers: { "content-type": "application/json" },
        credentials: "include",
        body: JSON.stringify(booking)
      });
      const result = await res.json();
      if (!res.ok) {
        return alert(result.message);
      }
      navigate("/thank-you");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div>
      <Form onSubmit={handleClick} className="booking-form">
        {/* Personal Information Fields */}
        <div className="form-group double">
          <input
            type="text"
            name="fullName"
            placeholder="Name"
            required
            id="fullName"
            onChange={handleChange}
            value={booking.fullName.split(' ')[0]} // Assuming the first part is the name
          />
          <input
            type="text"
            name="surname"
            placeholder="Surname"
            required
            id="surname"
            onChange={handleChange}
            value={booking.fullName.split(' ')[1]} // Assuming the second part is the surname
          />
        </div>
        { !user && (
          <FormGroup>
            <input
              type="email"
              id="userEmail"
              placeholder="Enter your email"
              value={booking.userEmail}
              onChange={handleChange}
              required
            />
          </FormGroup>
        )}
        <div className="form-group double">
          <input
            type="text"
            id="passengerNames"
            placeholder="Passengers Names"
            value={booking.passengerNames}
            required
            onChange={handleChange}
          />
          <input
            type="text"
            id="phone"
            placeholder="Phone"
            required
            value={booking.phone}
            onChange={handleChange}
          />
        </div>

        {/* Transfer Information Fields */}
        <FormGroup>
          <FormGroup>
            <p>Transfer From: {booking.transferFrom}</p>
            <button className="btn swap-btn" onClick={handleSwap}>Swap</button>
            <p>Transfer To: {booking.transferTo}</p>
            <input
              type="text"
              id="address"
              placeholder="Exact Address for Pick-Up or Drop-Off"
              value={booking.address}
              onChange={handleChange}
              required
            />
          </FormGroup>
          <input
            type="text"
            id="flightNumber"
            placeholder="Flight number"
            value={booking.flightNumber}
            required
            onChange={handleChange}
          />
          <input
            type="date"
            id="transferDate"
            required
            value={booking.transferDate}
            onChange={handleChange}
          />
          <input
            type="time"
            id="transferTime"
            required
            value={booking.transferTime}
            onChange={handleChange}
          />
          <textarea
            id="note"
            placeholder="Note"
            onChange={handleChange}
            value={booking.note}
          />
        </FormGroup>
      </Form>
      <Button onClick={handleClick} className="btn primary__btn">
        Book Now - ${price}
      </Button>
    </div>
  );
};

export default TransferBooking;
