import React, { useState, useContext } from "react";
import { Form, FormGroup, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import "./transferBooking.css";
import { BASE_URL } from "../../utils/config";


const TransferBooking = ({ transfer }) => {
  const { price } = transfer;
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const [booking, setBooking] = useState({
    userId: user ? user._id : null,
    userEmail: user ? user.email : "",
    transferFrom: "",
    transferTo: "",
    flightNumber: "",
    fullName: "",
    phone: "",
    passengerNames: "",
    transferDate: "",
    transferTime: "",
    note: ""
  });


  const handleChange = e => {
    setBooking(prev=>({...prev,[e.target.id]:e.target.value})
    );
  };

  const handleClick = async e => {
    e.preventDefault();


    try {
      const res = await fetch(`${BASE_URL}/transfer-booking`, {
        method:"post",
        headers:{"content-type":"application/json"},
        credentials:"include",
        body:JSON.stringify(booking)
    });
    const result=await res.json();
    if(!res.ok) {
      return alert(result.message);
    }
    navigate("/thank-you");


} catch (error) {
  alert(error.message);

}
  };


  return (
    <Form onSubmit={handleClick} className="booking-form">
      {/* Personal Information Fields */}
      <FormGroup>
        <input
          type="text"
          name="fullName"
          placeholder="Name Surname"
          required
          id="fullName"


          onChange={handleChange}
        />
        { !user && (
        <FormGroup>
          <input
            type="email"
            name="userEmail"
            placeholder="Enter your email"
            value={booking.userEmail}
            onChange={handleChange}
            required
          />
        </FormGroup>
      )}
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          required
          value={booking.phone}  

          onChange={handleChange}
        />
        <input
          type="text"
          name="passengerNames"
          placeholder="Passengers Names"
          value={booking.passengerNames}  

          onChange={handleChange}
        />
      </FormGroup>

      {/* Transfer Information Fields */}
      <FormGroup>
        <input
          type="text"
          name="transferFrom"
          placeholder="From"
          required
          value={booking.transferFrom}  

          onChange={handleChange}
        />
        <input
          type="text"
          name="transferTo"
          placeholder="To"
          required
          value={booking.transferTo}  

          onChange={handleChange}
        />
        <input
          type="text"
          name="flightNumber"
          placeholder="Flight number"
          value={booking.flightNumber}  

          onChange={handleChange}
        />
        <input
          type="date"
          name="transferDate"
          required
          value={booking.transferDate}  

          onChange={handleChange}
        />
        <input
          type="time"
          name="transferTime"
          required
          value={booking.transferTime}  

          onChange={handleChange}
        />
        <textarea
          name="note"
          placeholder="Note"
          onChange={handleChange}
          value={booking.note}  

        />
      </FormGroup>

      <Button  onClick={handleClick} className="btn primary__btn">
        Book Now - ${price}
      </Button>
    </Form>
  );
};

export default TransferBooking;
