import React from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import  calculateAvgRating  from "../utils/avgRating";

import "./transfer-card.css"; // Assuming a separate CSS file for transfer cards

const TransferCard = ({ transfer }) => {
  const { _id, name, location, photo, price, type, reviews } = transfer;

  // Assuming a function to calculate average rating, similar to tours
  const { totalRating, avgRating } = calculateAvgRating(reviews); 

  return (
    <div className="transfer__card">
      <Card>
        <div className="transfer__img">
          <img src={photo} alt="transfer-img" />
          {type && <span>{type}</span>} {/* Displaying type if available */}
        </div>
        <CardBody>
          <div className="card__top d-flex align-items-center justify-content-between">
            <span className="transfer__location d-flex align-items-center gap-1 ">
              <i class="ri-map-pin-line"></i> {location}
            </span>
            <span className="transfer__rating d-flex align-items-center gap-1 ">
              <i class="ri-star-fill"></i> {avgRating === 0 ? null : avgRating}
              {totalRating === 0 ? (
                "Not rated"
              ) : (
                <span> ({reviews.length}) </span>
              )}
            </span>
          </div>

          <h5 className="transfer__title">
            <Link to={`/transfers/${_id}`}>{name}</Link>
          </h5>
          <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
            <h5>
              ${price} <span>/per transfer</span>
            </h5>
            <button className="btn booking__btn">
              <Link to={`/transfers/${_id}`}>Book Now</Link>
            </button>
          </div>
        </CardBody>
      </Card>
    </div>
  );
};

export default TransferCard;
