import React, { useState, useContext } from "react";
import { Form, FormGroup, Button } from "reactstrap";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

const TransferBooking = ({ transfer }) => {
  const { price } = transfer;
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const initialBookingState = {
    userId: user ? user._id : null,
    userEmail: user ? user.email : "",
    transferFrom: "",
    transferTo: "",
    flightNumber: "",
    fullName: "",
    email: "",
    phone: "",
    passengerNames: "",
    transferDate: "",
    transferTime: "",
    note: ""
  };

  const [booking, setBooking] = useState(initialBookingState);

  const handleChange = e => {
    setBooking({ ...booking, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    // Implement the booking logic here
    // ...
    navigate("/thank-you"); // Redirect after booking
  };

  return (
    <Form onSubmit={handleSubmit} className="booking-form">
      {/* Personal Information Fields */}
      <FormGroup>
        <input
          type="text"
          name="fullName"
          placeholder="Name Surname"
          required
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="E-mail"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="passengerNames"
          placeholder="Passengers Names"
          onChange={handleChange}
        />
      </FormGroup>

      {/* Transfer Information Fields */}
      <FormGroup>
        <input
          type="text"
          name="transferFrom"
          placeholder="From"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="transferTo"
          placeholder="To"
          required
          onChange={handleChange}
        />
        <input
          type="text"
          name="flightNumber"
          placeholder="Flight number"
          onChange={handleChange}
        />
        <input
          type="date"
          name="transferDate"
          required
          onChange={handleChange}
        />
        <input
          type="time"
          name="transferTime"
          required
          onChange={handleChange}
        />
        <textarea
          name="note"
          placeholder="Note"
          onChange={handleChange}
        />
      </FormGroup>

      <Button type="submit" className="btn primary__btn">
        Book Now - ${price}
      </Button>
    </Form>
  );
};

export default TransferBooking;
